function parseConcatenatedJSON(str) {
    // Split the string on line breaks, handling both Unix (\n) and Windows (\r\n) line endings
    const lines = str.split(/\r?\n/);
    const result = [];

    for (const line of lines) {
        const trimmedLine = line.trim();
        if (trimmedLine) { // Skip empty lines
            try {
                result.push(JSON.parse(trimmedLine));
            } catch (e) {
                console.error('Failed to parse JSON:', trimmedLine, e);
                // Handle parsing error if necessary
            }
        }
    }
    return result;
}

msg.payload = parseConcatenatedJSON(msg.payload);

return msg;

