// Constants for generating multipart/form-data
const boundary = '--multipart-boundary-' + Math.random().toString().substring(2);
const crlf = '\r\n';
const delimiter = '--' + boundary;

// Build the multipart/form-data payload
let payload = '';
payload += delimiter + crlf;
payload += 'Content-Disposition: form-data; name="attach"' + crlf + crlf;
payload += '0' + crlf; // set attach to 0 to replace existing file
payload += delimiter + crlf;
payload += 'Content-Disposition: form-data; name="field_name"' + crlf + crlf;
payload += 'field_upload' + crlf;
payload += delimiter + crlf;
payload += 'Content-Disposition: form-data; name="files[1]"; filename="' + fileName + '"' + crlf;
payload += 'Content-Type: text/csv' + crlf + crlf;
payload += csvContent + crlf;
payload += delimiter + '--' + crlf;

msg.headers = {
    'Content-Type': 'multipart/form-data; boundary=' + boundary,
    'Cookie': sessionInfo.session_name + '=' + sessionInfo.sessid,
