// Get the query results
const results = msg.payload;

// Check if results are available
if (!results || results.length === 0) {
    node.error("No results returned from query", msg);
    return null;
}

// Function to format date
function formatDate(dateString) {
    const date = new Date(dateString);
    const year = date.getUTCFullYear();
    const month = String(date.getUTCMonth() + 1).padStart(2, '0');
    const day = String(date.getUTCDate()).padStart(2, '0');
    const hours = String(date.getUTCHours()).padStart(2, '0');
    const minutes = String(date.getUTCMinutes()).padStart(2, '0');
    const seconds = String(date.getUTCSeconds()).padStart(2, '0');
    return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}:00Z`;
}

// Create a number formatter for 'de-DE' locale
const numberFormatter = new Intl.NumberFormat('de-DE', {
    minimumFractionDigits: 0,
    useGrouping: false
});

// Function to format numbers using Intl.NumberFormat
function formatNumber(value) {
    if (typeof value === 'number') {
        return numberFormatter.format(value);
    }
    return value;
}

function formatGeoJson(value) {
    return '"' + value.replaceAll('"', "'") + '"'
}

function getYesterdayDate() {
    const today = new Date();
    today.setDate(today.getDate() - 1);
    return today.toISOString().split('T')[0];
}

// Define CSV headers
const csvHeaders = 'id;name;type;dateobserved;location;atmosphericpressure;precipitation;relativehumidity;temperature;winddirection;windspeedavg;windspeedmax;windspeedmin';

// Map query results to CSV lines
const csvLines = results.map(row => {
    return [
        row.id,
        row.name,
        row.type,
        formatDate(row.dateobserved),
        formatGeoJson(row.location),
        formatNumber(row.atmosphericpressure),
        formatNumber(row.precipitation),
        formatNumber(row.relativehumidity),
        formatNumber(row.temperature),
        formatNumber(row.winddirection),
        formatNumber(row.windspeedavg),
        formatNumber(row.windspeedmax),
        formatNumber(row.windspeedmin)
    ].join(';');
});

// Combine headers and lines into a single CSV string
const csvContent = [csvHeaders, ...csvLines].join('\n');

// Convert the CSV content to a Buffer
//const csvBuffer = Buffer.from(csvContent, 'utf8');

// Set the CSV file as the payload
msg.csvContent = csvContent;

// Set the filename for the CSV file
msg.filename = `${msg.station.title}.csv`;

// Set the appropriate content type for CSV
msg.headers = {
    'Content-Type': 'text/csv',
    'Content-Disposition': 'attachment; filename="' + msg.filename + '"'
};

return msg;

